import City from "City";
import React, { Component } from "react";
import "Cities/css/Cities.css";

/**
 * City selector
 * @reactProps {Array} cities List of cities in selector
 * @reactProps {string} city Cty selecetd by default
 * @reactProps {Function} change Call on change city
 */
class Cities extends Component {
  /**
   * @ignore
   */
  constructor(props) {
    super(props);
    /**
     * @ignore
     */
    this.state = {
      cities: props.cities || [],
      errors: [],
      city: props.city || "",
      change: props.change || (() => {}),
      weather: null,
      weather_date: null
    };
  }

  /**
   * Expose data for external usage for react component.
   */
  get value() {
    return {
      city: this.state.city,
      weather: this.state.weather,
      weather_date: this.state.weather_date
    };
  }

  /**
   * Handle change of city. Get weather from remote
   * Call props.change if any
   */
  handleChange(e) {
    const city = e.target.value;
    this.setState(
      {
        city,
        weather: null,
        weather_date: null,
        errors: []
      },
      () => {
        this.state.change(this.value);
        this.loadCityWeather(city);
      }
    );
  }

  /**
   * Redraw weather data on create component
   */
  componentDidMount() {
    this.loadCityWeather(this.state.city);
  }

  /**
   * Load weather from openweathermap.org
   */
  loadCityWeather(city) {
    clearTimeout(this.state.timeout);
    this.setState({
      timeout: setTimeout(() => {
        this.loadCityWeather(city);
      }, 1000 * 60 * 30)
    });
    fetch(
      `https://api.openweathermap.org/data/2.5/weather?q=${
        city
      }&APPID=bd5e378503939ddaee76f12ad7a97608`
    )
      .then(resp => {
        if (resp.ok) {
          return resp.json();
        }
      })
      .then(json => {
        this.setState(
          {
            weather_date: new Date(),
            weather: json
          },
          () => {
            this.state.change(this.value);
          }
        );
      });
  }

  /**
   * @ignore
   */
  render() {
    let cities = this.state.cities.map((x, id) => (
      <option key={id} value={x}>
        {x}
      </option>
    ));
    if (this.state.city === "") {
      cities = [<option key={-1}>Select city</option>, ...cities];
    }
    return (
      <div className="cities">
        <select value={this.state.city} onChange={this.handleChange.bind(this)}>
          {cities}
        </select>
        <City
          weather={this.state.weather}
          updated={this.state.weather_date}
          city={this.state.city}
        />
      </div>
    );
  }
}
export default Cities;

import PropTypes from "prop-types";
import React, { Component } from "react";
import "City/css/open-weather-icons.css";
import "City/css/City.css";
import _ from "lodash";
/**
 * Dumb component for output city weather information.
 * @reactProps {object} weather Data in format of https://openweathermap.org/current
 * @reactProps {Array} weather.weather
 * @reactProps {string} weather.weather[0].icon Icon of current weather
 * @reactProps {object} weather.main
 * @reactProps {number} weather.main.temp Temperature
 */
class City extends Component {
  /**
   * @ignore
   */
  render() {
    let icon, temp;
    if (this.props.weather) {
      icon = (
        <i
          className={`owi owi-${_.get(
            this.props,
            "weather.weather[0].icon"
          )} city__weather`}
        />
      );
      temp = `${_.get(this.props, "weather.main.temp")}°`;
    }

    return (
      <div className="city">
        {icon}
        <div className="city__temp">{temp}</div>
      </div>
    );
  }
}

City.propTypes = {
  weather: PropTypes.object
};
export default City;

import Cities from "Cities";
import React, { Component } from "react";
import _ from "lodash";
import "Form/css/Form.css";
/**
 * Wheather form.
 * -----
 * It contains wheather selector by city, comment field and submit button
 * @reactProps {Function(state)} onSubmit
 * @reactProps {Array} cities List of cities
 */
class Form extends Component {
  /**
   * @property {Array} list City data list
   * @property {string} comment Unsaved comment field
   * @property {object} info Unsaved weather info
   * @property {Boolean} error Error flag
   */
  static get defaultProps() {
    return {
      list: [],
      comment: "",
      info: null,
      error: false
    };
  }

  /**
   * @ignore
   */
  constructor(props) {
    super(props);
    /**
     * @ignore
     */
    this.state = _.assign({}, Form.defaultProps, props.defaults);
  }

  /**
   * Handle submit
   */
  handleSubmit(e) {
    e.preventDefault();
    const state = _.clone(this.state, true);
    if (state.info.city !== "") {
      state.error = false;
      state.list.push({
        comment: state.comment,
        info: state.info
      });
      state.comment = "";
    } else {
      state.error = true;
    }
    this.setState(state, () => {
      if (this.props.onSubmit !== undefined) {
        this.props.onSubmit(this.state);
      }
    });
  }

  /**
   * Handle change city
   */
  changeCity(info) {
    this.setState({ info });
  }

  /**
   * Handle change comment
   */
  changeComment(e) {
    this.setState({ comment: e.target.value });
  }

  /**
   * Render single item of table
   */
  listItem(data, id) {
    let wd = _.get(data, "info.weather_date");
    wd = wd === undefined ? "—" : wd.toString();
    return (
      <tr key={id}>
        <td>{data.info.city}</td>
        <td>{wd}</td>
        <td>{_.get(data, "info.weather.main.temp", "—")}</td>
        <td>{_.get(data, "info.weather.weather[0].description", "—")}</td>
        <td>{data.comment}</td>
      </tr>
    );
  }

  /**
   * @ignore
   */
  render() {
    const table =
      this.state.list.length > 0 ? (
        <table className="form__table">
          <thead>
            <tr>
              <th>Cty</th>
              <th>Weather date</th>
              <th>Temp</th>
              <th>Weather</th>
              <th>Comment</th>
            </tr>
          </thead>
          <tbody>
            {this.state.list.map((data, id) =>
              this.listItem(data, this.state.list.length - id)
            )}
          </tbody>
        </table>
      ) : (
        <div className="form__table">
          No weather info yet. Left ther first one.
        </div>
      );
    let error;
    if (this.state.error) {
      error = <div class="form__error">Please select the city</div>;
    }
    return (
      <div>
        {table}
        <form onSubmit={this.handleSubmit.bind(this)} className="form">
          {error}
          <Cities
            cities={this.props.cities}
            change={this.changeCity.bind(this)}
            city={_.get(this.state, "info.city")}
          />

          <textarea
            className="form__comment"
            value={this.state.comment}
            onChange={this.changeComment.bind(this)}
          />
          <div className="form__submit">
            <input type="submit" value="Add" />
          </div>
        </form>
      </div>
    );
  }
}

export default Form;

import Cities from "Cities";
import Form from "Form";
import React from "react";
import ReactDOM from "react-dom";
import UniversalRouter from "universal-router";
import registerServiceWorker from "./registerServiceWorker";

/**
 * @const store Storage abstraction
 */
const store = require("store");

/**
 * Load list of world capitals.
 */
function loadCities() {
  return fetch(
    "https://cors-anywhere.herokuapp.com/http://country.io/capital.json"
  )
    .then(resp => {
      if (!resp.ok) {
        throw Error(resp.statusText);
      }
      return resp.json();
    })
    .then(resp => {
      const cities = [];
      for (const cc in resp) {
        if (resp[cc] !== "") {
          cities.push(resp[cc]);
        }
      }
      return cities;
    });
}

/**
 * Save state of selector to history api.
 */
function changeCity(data) {
  window.history.pushState({ city: data.city }, null, `/city/${data.city}`);
}

/**
 * Save state of component to abstract storage.
 */
function saveList(data) {
  store.set("form", JSON.stringify(data));
}

/**
 * Routes
 */
const routes = [
  {
    path: "/",
    async action({ params, next }) {
      const cities = await loadCities();
      params.cities = cities;
      return await next();
    },
    children: [
      {
        path: "/city/:city",
        action: (context, { city, cities }) => (
          <Cities city={city} cities={cities} change={changeCity} />
        )
      },
      {
        path: "/city",
        action: (context, { cities }) => (
          <Cities cities={cities} change={changeCity} />
        )
      },
      {
        path: "(.*)",
        action: (context, { cities }) => {
          let data = store.get("form");
          if (data !== undefined) {
            data = JSON.parse(data);
          }
          return <Form cities={cities} onSubmit={saveList} defaults={data} />;
        }
      }
    ]
  }
];
/**
 * @const router Universal router
 */
const router = new UniversalRouter(routes);

router.resolve({ pathname: window.location.pathname }).then(rootComponent => {
  ReactDOM.render(rootComponent, document.getElementById("root"));
  registerServiceWorker();
});

